mapper.update:
UPDATE t_bug SET object_code = #{object_code, jdbcType=VARCHAR}, model_code = #{model_code, jdbcType=VARCHAR}, title = #{title, jdbcType=VARCHAR}, description = #{description, jdbcType=VARCHAR}, principal = #{principal, jdbcType=VARCHAR}, parent_code = #{parent_code, jdbcType=VARCHAR}, version_code = #{version_code, jdbcType=VARCHAR}, priority = #{priority, jdbcType=VARCHAR}, bug_level = #{bug_level, jdbcType=VARCHAR}, result = #{result, jdbcType=VARCHAR},
deadline = #{deadline},
update_time = sysdate(), state = #{state, jdbcType=VARCHAR} WHERE uuid = #{uuid};

mapper.select:
SELECT CODE,TITLE,ISSUE_TYPE
  FROM PESTICIDE.V_ISSUE
 WHERE CODE LIKE concat('%',#{code},'%') AND OBJECT_CODE = #{objectCode}
