mapper.update:
UPDATE

    t_task


    t_bug

SET VERSION_CODE = #{version_code}
WHERE UUID = #{uuid}

UPDATE

    t_task


    t_bug

SET STATE = '10'
WHERE VERSION_CODE = #{versionCode}
  AND OBJECT_CODE = #{objectCode}

mapper.select:
select model_code from v_issue
WHERE object_code = #{objectCode}
and version_code = #{versionCode}
group by model_code
