mapper.update:
UPDATE t_task t SET t.state = '4' WHERE t.uuid IN (SELECT tp.business_id FROM t_push tp WHERE tp.model_code IN
#{item}
AND tp.push_state = '0' AND tp.issue_type = 'T')

UPDATE t_bug t SET t.state = '4' WHERE t.uuid IN (SELECT tp.business_id FROM t_push tp WHERE tp.model_code IN
#{item}
AND tp.push_state = '0' AND tp.issue_type = 'B')

UPDATE t_push tp SET tp.push_state = '1', tp.push_time = sysdate(), tp.push_user = #{operator}, tp.push_version = date_format(sysdate(),'%Y%m%d%H%i%S') WHERE tp.model_code IN
#{item}
AND tp.push_state = '0'

mapper.select:
SELECT DISTINCT V.uuid FROM T_PUSH P LEFT JOIN V_ISSUE V ON P.BUSINESS_ID = V.UUID WHERE P.MODEL_CODE IN
#{item}
AND V.OBJECT_CODE = #{object_code} AND P.PUSH_STATE = '0'
